'use strict';

module.exports = apiPlugin;

var get = require('lodash/get');

var pick = require('lodash/pick');

var method = require('./method');

var ENDPOINT_DEFAULTS = require('../../routes.json');

function apiPlugin(octokit) {
  Object.keys(ENDPOINT_DEFAULTS).forEach(function (namespaceName) {
    octokit[namespaceName] = {};
    Object.keys(ENDPOINT_DEFAULTS[namespaceName]).forEach(function (apiName) {
      var apiOptions = ENDPOINT_DEFAULTS[namespaceName][apiName];
      var deprecated;

      if (apiOptions.alias) {
        deprecated = apiOptions.deprecated;
        apiOptions = get(ENDPOINT_DEFAULTS, apiOptions.alias);
      }

      var endpointDefaults = pick(apiOptions, ['method', 'url', 'headers', 'request']);

      if (deprecated) {
        endpointDefaults.deprecated = deprecated;
      }

      octokit[namespaceName][apiName] = method.bind(null, octokit, endpointDefaults, apiOptions.params);
    });
  });
}
