module.exports = getPage;

var HttpError = require('../../request/http-error');

var getPageLinks = require('./get-page-links');

function getPage(octokit, link, which, headers, callback) {
  if (typeof headers === 'function') {
    callback = headers;
    headers = null;
  }

  var url = getPageLinks(link)[which];

  if (!url) {
    var urlError = new HttpError("No ".concat(which, " page found"), 404);

    if (callback) {
      return callback(urlError);
    }

    return Promise.reject(urlError);
  }

  var requestOptions = {
    url: url,
    headers: applyAcceptHeader(link, headers)
  };
  var promise = octokit.request(requestOptions);

  if (callback) {
    promise.then(callback.bind(null, null), callback);
    return;
  }

  return promise;
}

function applyAcceptHeader(res, headers) {
  var previous = res.headers && res.headers['x-github-media-type'];

  if (!previous || headers && headers.accept) {
    return headers;
  }

  headers = headers || {};
  headers.accept = 'application/vnd.' + previous.replace('; param=', '.').replace('; format=', '+');
  return headers;
}
