import React, { Component } from 'react';
import './App.css';
import Form from './Form.js';

class App extends Component {

  constructor (props) {
    super(props);
    this.state = {
      username: '',
      formSubmitted: false,
      data: '',
    };
  }

  handleChange(event) {
    this.setState({username: event.target.value});
  }

  handleSubmit(event) {
    //console.log('A name was submitted: ' + this.state.username);
    event.preventDefault();
    this.state.username = this.state.username;
    this.props.submitted = true;
    console.log('A name was submitted: ' + this.props.username);
    console.log('this.props.submitted: ' + this.props.submitted);
    //octokit.users.getForUser({username: this.state.username}).then(result => {console.log(result)});
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={ require('./images/gh2.png') } className="App-logo" alt="logo" />
          <Form onChangeValue={this.handleChange} onSubmit={this.handleSubmit}/>
        </header>
      </div>
    );
  }
}

export default App;
