module.exports = authenticationBeforeRequest;

var btoa = require('btoa-lite');

var uniq = require('lodash/uniq');

var deprecate = require('../../deprecate');

function authenticationBeforeRequest(state, options) {
  if (!state.auth.type) {
    return;
  }

  if (state.auth.type === 'basic') {
    var hash = btoa("".concat(state.auth.username, ":").concat(state.auth.password));
    options.headers['authorization'] = "Basic ".concat(hash);
    return;
  }

  if (state.auth.type === 'token') {
    options.headers['authorization'] = "token ".concat(state.auth.token);
    return;
  } // deprecate state.auth.type === 'integration', rename to 'app'


  if (state.auth.type === 'integration') {
    deprecate('authentication type "integration" is deprecated. Use "app" instead.');
    state.auth.type = 'app';
  }

  if (state.auth.type === 'app') {
    options.headers['authorization'] = "Bearer ".concat(state.auth.token);
    var acceptHeaders = options.headers['accept'].split(',').concat('application/vnd.github.machine-man-preview+json');
    options.headers['accept'] = uniq(acceptHeaders).filter(Boolean).join(',');
    return;
  }

  options.url += options.url.indexOf('?') === -1 ? '?' : '&';

  if (state.auth.token) {
    options.url += "access_token=".concat(encodeURIComponent(state.auth.token));
    return;
  }

  var key = encodeURIComponent(state.auth.key);
  var secret = encodeURIComponent(state.auth.secret);
  options.url += "client_id=".concat(key, "&client_secret=").concat(secret);
}
